    if err != nil {
        fmt.Println(&quot;Execute error &quot; + err.Error())
        http.Error(rw, err.Error(), http.StatusInternalServerError)
        return
    }

}

func checkError(err error) {
    if err != nil {
        fmt.Println(&quot;Fatal error &quot;, err.Error())
        os.Exit(1)
    }
}

